package ForLoopNsteadPatternProgramme;

public class EvenOdd {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p;
		for(p=1; p<=15; p++)
		{
			if(p % 2 == 1) {
			System.out.print(p+ " ");
			}
			}
			
		int i;		
		for(i=1; i<=15; i++)
		{
			if(i % 2 == 0) {
			System.out.print(i+ " ");
			}
			}
			}
}
